import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
// import Byline from '../molecules/Byline';

const StyledLink = styled(Link)`
  text-decoration: none;
`;

const images = {
  football: "https://ugc.futurelearn.com/uploads/images/90/2d/902d0c48-095e-4919-81aa-4b8f4d3f198c.jpg",
  cooking: "https://www.yesmagazine.org/wp-content/uploads/imports/36a0edc6dcbf4466ae71d0548f94ff43.jpg",
  coding: "https://miro.medium.com/max/3200/0*QUqE4WGF8_cC9bIl.jpg"
}

const Card = styled('article')`
  position: relative;
  background-color: #fff;
  border: solid 1px #eee;
  border-radius: 1rem;
  margin-bottom: 1rem;
  box-shadow: 0.25rem 0.25rem 0.25rem #ddd;
  padding: 2rem;
  width: calc(50% - 3rem);
  overflow: hidden;

  h2 {
    font-family: "Zilla Slab", serif;
    font-size: 1.5rem;
    font-weight: 600;
    text-decoration: none;
    margin: 0;
  }

  p {
    margin: 0;
  }
`;


const ArticleCard = ({ article }) => {
  const CardImage = styled.div`
    margin: -2rem 0 1rem -2rem;
    width: calc(100% + 4rem);
    background-image: url(${images[article.belongs_to]});
    background-size: cover;
    height: 15rem;
  `;

  return (
    <StyledLink to={'/articles/' + article.article_id}>
      <Card>
        <CardImage />
        <h2>{article.title}</h2>
        {/* <Byline authorUsername={author} /> */}
        <p>by {article.created_by} in {article.belongs_to}</p>
      </Card>
    </StyledLink>
  );
};

ArticleCard.propTypes = {
  article: PropTypes.object.isRequired
};

export default ArticleCard;
