import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import dayjs from 'dayjs';

const StyledComment = styled.section`
  margin-bottom: 1rem;
  border-radius: 5px;
  border: solid 1px #ddd;
  box-shadow: 1px 1px 3px #eee;
  background-color: #fff;
  padding: 1rem;
  overflow: hidden;
  word-wrap: break-word;

  h3, p {
    margin: 0;
  }
`;

class Comment extends React.Component {
  componentDidMount() {
    this.fetchUser(this.props.comment.created_by);
  }

  render () {
    const timestamp = Number(this.props.comment.created_at);
    return (
      <StyledComment>
        <h3>{this.props.comment.created_by} says:</h3>
        <p className="commentBody">{this.props.comment.body}</p>
        <span className="commentInfo">Posted at {dayjs(timestamp)}<br />
          Votes: {this.props.comment.votes} </span>
      </StyledComment>
    );
  }
}

Comment.propTypes = {
  comment: PropTypes.object.isRequired
};

export default Comment;
