import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import dayjs from 'dayjs';
import * as actions from '../actions/actions';

const StyledComment = styled.section`
  margin-bottom: 1rem;
  border-radius: 5px;
  border: solid 1px #ddd;
  box-shadow: 1px 1px 3px #eee;
  background-color: #fff;
  padding: 1rem;
  overflow: hidden;
  word-wrap: break-word;

  h3, p {
    margin: 0;
  }
`;

const Avatar = styled.img`
  width: 2rem;
  height: 2rem;
  border-radius: 1rem;
`;

class Comment extends React.Component {
  componentDidMount() {
    this.props.fetchAllUsers();
  }

  render () {
    const time = dayjs(this.props.comment.created_at).format('HH:mm DD MM YYYY');
    console.log('Comment > this.props.users:', this.props.users);
    console.log('this.state', this.state);
    const thisUser = this.props.users.filter(user => user.username === this.props.comment.created_by)[0]
    return (
      <StyledComment>
        <img src={thisUser.avatar_url} alt={thisUser.name} />
        <h3>{thisUser.name} says:</h3>
        <p className="commentBody">{this.props.comment.body}</p>
        <span className="commentInfo">Posted at {time}<br />
          Votes: {this.props.comment.votes} </span>
      </StyledComment>
    );
  }
}

function mapDispatchToProps (dispatch) {
  return {
    fetchAllUsers: () => {
      console.log('fetching users...');
      dispatch(actions.fetchAllUsers());
    },
  };
}

function MapStateToProps (state) {
  return {
    users: state.allUsers,
    loading: state.loading
  };
}

Comment.propTypes = {
  comment: PropTypes.object.isRequired,
  users: PropTypes.object
};

export default connect(MapStateToProps, mapDispatchToProps) (Comment);
