import React from 'react';
import PropTypes from 'prop-types';
// import styled from 'styled-components';
import { connect } from 'react-redux';
import * as actions from '../../actions/actions';

class Byline extends React.Component {
  componentDidMount () {
    this.props.fetchUser(this.props.authorUsername);
  }

  shouldComponentUpdate(nextProps) {
    return nextProps.authorUsername !== this.props.authorUsername;
  }
  componentDidUpdate () {
    this.props.fetchUser(this.props.authorUsername);
    this.setState({
      author: this.props.author.filter(user => user.username === this.props.authorUsername)[0]
    })
  }

  render () {
    console.log('Byline > this.props.authorUsername:', this.props.authorUsername, typeof this.props.authorUsername);
    console.log('Byline author:', this.props.author, typeof this.props.author);
    const trueName = () => {
      if (!this.props.author) return null;
      return this.props.author.filter(user => user.username === this.props.authorUsername)[0];
    }
    console.log('trueName', trueName());
    return <p>written by {this.props.authorUsername}</p>;
  }
}

function mapDispatchToProps (dispatch) {
  return {
    fetchUser: (authorUsername) => {
      dispatch(actions.fetchUser(authorUsername));
    },
  };
}

function MapStateToProps (state) {
  return {
    author: state.user,
    loading: state.loading
  };
}
Byline.defaultProps = {
  authorUsername: ""
}

Byline.propTypes = {
  author: PropTypes.object,
  authorUsername: PropTypes.string,
  loading: PropTypes.bool.isRequired,
  fetchUser: PropTypes.func.isRequired,
};

export default connect(MapStateToProps, mapDispatchToProps) (Byline);
