import React from 'react';
import PropTypes from 'prop-types';
// import styled from 'styled-components';
import { connect } from 'react-redux';
import * as actions from '../../actions/actions';

class Byline extends React.Component {
  componentDidMount () {
    this.props.fetchUser(this.props.selectedArticle.created_by);
  }

  render () {
    console.log('Byline user:', this.props.user);
    return (
      <div>
        <p>written by {this.props.user.name}</p>
      </div>
    );
  }
}

function mapDispatchToProps (dispatch) {
  return {
    fetchUser: (username) => {
      dispatch(actions.fetchUser(username));
    },
  };
}

function MapStateToProps (state) {
  return {
    user: state.user,
    loading: state.loading
  };
}

Byline.propTypes = {
    username: PropTypes.string.isRequired,
    loading: PropTypes.bool.isRequired,
    fetchUser: PropTypes.func.isRequired,
};

export default connect(MapStateToProps, mapDispatchToProps) (Byline);
