import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import dayjs from 'dayjs';
import * as actions from '../actions/actions';

const StyledComment = styled.section`
  margin-bottom: 1rem;
  border-radius: 5px;
  border: solid 1px #ddd;
  box-shadow: 1px 1px 3px #eee;
  background-color: #fff;
  padding: 1rem;
  overflow: hidden;
  word-wrap: break-word;

  h3, p {
    margin: 0;
  }
`;

class Comment extends React.Component {
  componentDidMount() {
    this.props.fetchUser(this.props.comment.created_by);
  }

  shouldComponentUpdate() {
    return typeof this.props.user === "undefined";
  }

  componentDidUpdate() {
    this.props.fetchUser(this.props.comment.created_by);
  }

  render () {
    const time = dayjs(this.props.comment.created_at).format('HH:mm DD MM YYYY');
    console.log('Comment > this.props.user:', this.props.user);
    return (
      <StyledComment>
        <h3>{this.props.comment.created_by} says:</h3>
        <p className="commentBody">{this.props.comment.body}</p>
        <span className="commentInfo">Posted at {time}<br />
          Votes: {this.props.comment.votes} </span>
      </StyledComment>
    );
  }
}

function mapDispatchToProps (dispatch) {
  return {
    fetchUser: (username) => {
      dispatch(actions.fetchUser(username));
    },
  };
}

function MapStateToProps (state) {
  return {
    user: state.user,
    loading: state.loading
  };
}

Comment.propTypes = {
  comment: PropTypes.object.isRequired
};

export default connect(MapStateToProps, mapDispatchToProps) (Comment);
