import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import ArticleTitleBar from './ArticleTitleBar';
import ArticleText from './ArticleText';
import VoteButton from './VoteButton';
import NewComment from './NewComment';
import CommentList from './CommentList';
import { connect } from 'react-redux';
import * as actions from '../actions/actions';
import '../style.css';

const Article = styled.article`
  background-color: #fff;
  padding: 1rem;

  h2, p {
    margin: 0;
  }
`;

class Byline extends React.Component {
  componentDidMount () {
    this.props.fetchUser(this.props.selectedArticle.created_by);
  }

  render () {
    console.log('Byline user:', this.props.user);
    return (
      <div>
        <Article>
          <ArticleTitleBar 
            title={this.props.selectedArticle.title}
            author={this.props.selectedArticle.created_by} 
          />
          <ArticleText 
            body={this.props.selectedArticle.body} 
          />
        </Article>
          <hr />
          <div className="voteAndComment">
            <VoteButton 
              votes={this.props.selectedArticle.votes} 
              id={this.props.match.params.article_id}
            />
            <NewComment 
              postComment={this.props.addCommentsByArticleID}
              article_id={this.props.match.params.article_id} 
            />
          </div>
          <hr />
            <CommentList 
              selectedComments={this.props.selectedComments} 
            />               
        </div>
    );
  }
}

function mapDispatchToProps (dispatch) {
  return {
    fetchUser: (username) => {
      dispatch(actions.fetchUser(username));
    },
  };
}

function MapStateToProps (state) {
  return {
    user: state.user,
    loading: state.loading
  };
}

Byline.propTypes = {
    username: PropTypes.string.isRequired,
    loading: PropTypes.bool.isRequired,
    fetchUser: PropTypes.func.isRequired,
};

export default connect(MapStateToProps, mapDispatchToProps) (Byline);
