import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import styled from 'styled-components';
import ArticleCard from '../molecules/ArticleCard';
import MostRecent from '../organisms/MostRecent';
import * as actions from '../../actions/actions';

class Home extends React.Component {
  componentDidMount () {
    this.props.fetchArticles();
  }

  render () {
    if (!this.props.articles) return <p>No articles!</p>;
    const ArticleList = styled.section`
    display: flex;
    flex-flow: row wrap;
    justify-content: space-between;
    width: 100%;
    border: solid 1px red;
  `;
    console.log('Home > articles', this.props.articles);
    return (
      <React.Fragment>
        <MostRecent articles={this.props.articles} />
        <ArticleList>
          {this.props.articles.sort((a, b) => b.votes - a.votes).map(article => <ArticleCard article={article} key={article._id}/>)}
        </ArticleList>
      </React.Fragment>
    )
  }
}

Home.propTypes = {
  fetchArticles: PropTypes.func,
  articles: PropTypes.array
};

function mapDispatchToProps (dispatch) {
  return {
    fetchArticles: () => {
      dispatch(actions.fetchArticles());
    }
  };
}

function MapStateToProps (state) {
  return {
    articles: state.articles,
    loading :state.loading
  };
}

export default connect(MapStateToProps, mapDispatchToProps) (Home);
