import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import ArticleCard from '../molecules/ArticleCard';
import MostRecent from '../organisms/MostRecent';
import * as actions from '../../actions/actions';

class Home extends React.Component {
  componentDidMount () {
    this.props.fetchArticles();
  }

  render () {
    if (!this.props.articles) return <p>No articles!</p>;
    return (
      <React.Fragment>
        <MostRecent articles={this.props.articles} />
        {this.props.articles.sort((a, b) => b.votes - a.votes).map(article => <ArticleCard article={article} key={article._id}/>)}
      </React.Fragment>
    )
  }
}

Home.propTypes = {
  fetchArticles: PropTypes.func,
  articles: PropTypes.array
};

function mapDispatchToProps (dispatch) {
  return {
    fetchArticles: () => {
      dispatch(actions.fetchArticles());
    }
  };
}

function MapStateToProps (state) {
  return {
    articles: state.articles,
    loading :state.loading
  };
}

export default connect(MapStateToProps, mapDispatchToProps) (Home);
