import React from 'react';
import styled from 'styled-components';

const StyledProfile = styled.section`
  background-color: #fff;
  border-radius: 1rem;
  border: solid 1px #ddd;
  padding: 1rem;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
  align-items: flex-start;

  img {
    width: 3rem;
    height: 3rem;
    border-radius: 1.5rem;
  }
`;

const ProfileName = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;

const Profile = () => (
  <React.Fragment>
    <StyledProfile>
      <img className="avatar" src="https://avatars3.githubusercontent.com/u/6791502?v=3&s=200" alt="" />
      <ProfileName>
        <h2 className="title is-4">Awesome Northcoder</h2>
        <h3 className="subtitle is-6">northcoder</h3>
      </ProfileName>
      Northcoders News has all your latest updates.<br />
    </StyledProfile>
    <div>
    <hr />
      <form>
        <div className="search">
          <div className="control">
            <input id="search" className="input" type="text" placeholder="Search" />
          </div>
        </div>
      </form>
      <hr />
    </div>
  </React.Fragment>
);

export default Profile;
