import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { connect } from 'react-redux';
import * as actions from '../../actions/actions';

const StyledByline = styled.div`
  display: flex;
  align-items: center;
`;

const Avatar = styled.img`
  width: 1.5rem;
  height: 1.5rem;
  border-radius: 1rem;
  margin-right: 0.5rem;
  padding: 0.5rem 0;
`;

class Byline extends React.Component {
  componentDidMount () {
    this.props.fetchAllUsers();
  }

  render () {
    if (!this.props.users) return null;
    const thisUser = this.props.users.filter(user => user.username === this.props.username)[0];
    console.log('Byline > thisUser', thisUser);
    return (
    <StyledByline>
      <Avatar src={thisUser.avatar_url} alt={thisUser.name} />
      <a href={`/users/${this.props.username}`}>{thisUser.name} in {this.props.topic}</a>
    </StyledByline>
    );
  }
}

function mapDispatchToProps (dispatch) {
  return {
    fetchAllUsers: () => {
      dispatch(actions.fetchAllUsers());
    },
  };
}

function MapStateToProps (state) {
  console.log('Byline > state', state);
  return {
    users: state.allUsers,
    loading: state.loading
  };
}
Byline.defaultProps = {
  username: ""
}

Byline.propTypes = {
  users: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
  authorUsername: PropTypes.string,
  loading: PropTypes.bool.isRequired,
  fetchAllUsers: PropTypes.func.isRequired,
};

export default connect(MapStateToProps, mapDispatchToProps) (Byline);
