import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import ArticleCard from '../molecules/ArticleCard';

const MostRecentContainer = styled.section`
  display: flex;
  flex-flow: row nowrap;
`;

const MostRecent = ({ articles }) => (
  <>
    <h2>Most Recent...</h2>
    <MostRecentContainer>
      {articles.filter(article => article.belongs_to === "football")
        .map((article, index) => {
          if (index > 0) return null;
          return (
            <ArticleCard
              title={article.title}
              author={article.created_by}
              votes={article.votes} 
              key={article.title}
              topic={article.belongs_to} 
              article_id={article._id}
            />
        )})
      }
      {articles.filter(article => article.belongs_to === "cooking")
        .map((article, index) => {
          if (index > 0) return null;
          return (
            <ArticleCard
              title={article.title}
              author={article.created_by}
              votes={article.votes} 
              key={article.title}
              topic={article.belongs_to} 
              article_id={article._id}
            />
        )})
      }

      {articles.filter(article => article.belongs_to === "coding")
        .map((article, index) => {
          if (index > 0) return null;
          return (
            <ArticleCard
              title={article.title}
              author={article.created_by}
              votes={article.votes} 
              key={article.title}
              topic={article.belongs_to} 
              article_id={article._id}
            />
        )})
      }
    </MostRecentContainer>
  </>
);


MostRecent.defaultProps = {
  articles: []
}

MostRecent.propTypes = {
  fetchArticles: PropTypes.func,
  articles: PropTypes.array
};

export default MostRecent;


