import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import dayjs from 'dayjs';
import * as actions from '../actions/actions';

const StyledComment = styled.section`
  margin-bottom: 1rem;
  border-radius: 1rem;
  border: solid 1px #ddd;
  box-shadow: 1px 1px 3px #eee;
  background-color: #fff;
  padding: 1rem;
  overflow: hidden;
  word-wrap: break-word;

  h3, p {
    margin: 0;
  }
`;

const Commenter = styled.div`
  display: flex;
  flex-flow: row nowrap;
  justify-content: flex-start;
  align-items: center;

  p {
    font-size: 1.5rem;
    font-family: "Zilla Slab", serif;
  }
`;

const Avatar = styled.img`
  width: 2rem;
  height: 2rem;
  border-radius: 1rem;
  margin-right: 1rem;
`;

class Comment extends React.Component {
  componentDidMount() {
    this.props.fetchAllUsers();
  }

  render () {
    const time = dayjs(this.props.comment.created_at).format('HH:mm, DD MMMM YYYY');
    const thisUser = this.props.users.filter(user => user.username === this.props.comment.created_by)[0]
    return (
      <StyledComment>
        <Commenter>
          <Avatar src={thisUser.avatar_url} alt={thisUser.name} />
          <p><strong>{thisUser.name} says:</strong></p>
        </Commenter>
        <p className="commentBody">{this.props.comment.body}</p>
        <p>{time}</p>
        <span>{this.props.comment.votes} votes</span>
      </StyledComment>
    );
  }
}

function mapDispatchToProps (dispatch) {
  return {
    fetchAllUsers: () => {
      console.log('fetching users...');
      dispatch(actions.fetchAllUsers());
    },
  };
}

function MapStateToProps (state) {
  return {
    users: state.allUsers,
    loading: state.loading
  };
}

Comment.propTypes = {
  comment: PropTypes.object.isRequired,
  users: PropTypes.object
};

export default connect(MapStateToProps, mapDispatchToProps) (Comment);
