import React from 'react';
import styled from 'styled-components';
import dayjs from 'dayjs';

const StyledSection = styled.section`
  background-color: #fff;
  border-radius: 1rem;
  border: solid 1px #ddd;
  padding: 1rem;
  display: flex;
  flex-flow: row nowrap;
  justify-content: flex-start;
  align-items: flex-start;
  margin-bottom: 1rem;

  img {
    width: 5rem;
    height: 5rem;
    border-radius: 2.5rem;
    margin-right: 1rem;
  }

  h2, p {
    margin: 0;
  }
`;

const ProfileName = styled.div`
  height: 5rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;

console.log('dayjs locale?', dayjs.locale());

class Profile extends React.Component {
  componentDidMount() {
    this.props.fetchAllUsers();
  }

  render() {
    return (
      <React.Fragment>
        <StyledSection>
          <img src="https://avatars3.githubusercontent.com/u/6791502?v=3&s=200" alt="" />
          <ProfileName>
            <h2>Awesome Northcoder</h2>
            <p>northcoder</p>
          </ProfileName>
        </StyledSection>
        <StyledSection>
          <p>Welcome, Awesome Northcoder! It's {dayjs().format('MMMM DD, YYYY')}.</p>
        </StyledSection>
        <StyledSection>
          <form>
            <div className="search">
              <div className="control">
                <input id="search" className="input" type="text" placeholder="Search" />
              </div>
            </div>
          </form>
        </StyledSection>
      </React.Fragment>
    )
  }
}

export default Profile;
