import React from 'react';
import PropTypes from 'prop-types';
// import styled from 'styled-components';
import { connect } from 'react-redux';
import * as actions from '../../actions/actions';
// import ArticleText from '../atoms/ArticleText';
// import CommentList from '../organisms/CommentList';
// import VoteAndComment from '../organisms/VoteAndComment';
// import Byline from '../molecules/Byline';

// const Article = styled.article`
//   background-color: #fff;
//   padding: 1rem;

//   h2, p {
//     margin: 0;
//   }
// `;
import Profile from '../organisms/Profile';

class UserPage extends React.Component {
  componentDidMount () {
    this.props.fetchUser(this.props.match.params.user_id);
  }


  render () {
    console.log('looking for id', this.props.match.params.user_id);
    console.log('UserPage > props', this.props);
    return (
      <Profile user={this.props.user} />
    );
  }
}

function mapDispatchToProps (dispatch) {
  return {
    fetchUser: (id) => {
      dispatch(actions.fetchUser(id));
    }
  };
}

function MapStateToProps (state) {
  return {
    user: state.user
  };
}

UserPage.defaultProps = {
  user: {}
}

UserPage.propTypes = {
    user: PropTypes.object,
    fetchUser: PropTypes.func.isRequired,
    match: PropTypes.object.isRequired
};

export default connect(MapStateToProps, mapDispatchToProps) (UserPage);
