import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const Container = styled.div`
  max-width: 1280px;
  margin: 0 auto;
  display:flex;
  flex-flow: row nowrap;
  justify-content: space-between;
  align-items: center;
`;


const NavTab = styled(Link)`
  background-color:rgba(255,255,255,1);
  padding:1vw;
  margin:0 1vw; 
  min-width: 10vw !important;
  font-family: 'Zilla Slab', serif;
  font-size: calc(6px + 1vw);
  text-align:center;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  box-shadow: -1px -1px 3px #000;

  &:hover {
    background-color: rgba(200, 200, 255, 0.5);
    box-shadow: inset 1px 1px 3px #000;
    color:#fff;
  }
`;

const Title = styled.h1`
  font-family: 'Zilla Slab', serif;
  font-size:2rem;
  line-height:60px;
  color:#fff;
  text-shadow: -1px -1px 3px #000;
  white-space:nowrap;
  overflow:hidden;
`;

class Navbar extends React.Component {
  render() {
    return (
      <nav>
        <Container>
          <Title>Northcoders News</Title>
          <NavTab to="/">Home</NavTab>
          <NavTab to="/topics/football/articles">Football</NavTab>
          <NavTab to="/topics/cooking/articles">Cooking</NavTab>
          <NavTab to="/topics/coding/articles">Coding</NavTab>
        </Container>
      </nav>
    );
  }
}

export default Navbar;
